import React from "react";

export class Form extends React.Component {
    state = {
        count: 1,
        name: 'Dmitry'
    }

    handleClick() {
        this.setState({count: 2})
    }

    render(){
        return <form>
            <p>Count: {this.state.count}</p>
            <p>Name: {this.state.name}</p>
            <button type="button" onClick={this.handleClick}>Click</button>
        </form>
    }
}
